"use client";
import React from "react";
import {
  Col,
  Descriptions,
  DescriptionsProps,
  Row,
  Space,
  Tag,
  theme,
  Image,
  Skeleton,
  Divider,
  Tooltip,
  Result, Card,
} from "antd";
import LayoutDashboard from "@/components/Layouts/Dashboard";
import {
  AlertTwoTone,
  NotificationOutlined,
} from "@ant-design/icons";
import { Case, If, Then, Switch as SwitchIf, Else } from "react-if";
import { FormatDateTime } from "@/utils/date";
import { API_URL } from "@/config/config";
import Meta from "antd/es/card/Meta";

const DetailPage = ({ params }: { params: { uid: string } }) => {
  const [adminLevel, setAdminLevel] = React.useState<number>(0);

  const [loadingPage, setLoadingPage] = React.useState(false);

  const [detail, setDetail] = React.useState<any>({});

  const getData = async () => {
    setLoadingPage(true);
    const res = await fetch(`${API_URL}/member/${params.uid}/kyc`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    setDetail(data);
    setLoadingPage(false);
  };

  React.useEffect(() => {
    getData();

    const member = localStorage.getItem("admin");
    if (member) {
      const memberJson = JSON.parse(member);
      setAdminLevel(memberJson?.level);
    }
  }, []);

  const details: DescriptionsProps['items'] = [
    {
      key: '1',
      label: 'Full Name',
      children: detail?.kyc?.member_full_name || 'N/A',
    },
    {
      key: '2',
      label: 'Identity Card',
      children: detail?.kyc?.identity_card || 'N/A',
    },
    {
      key: '3',
      label: 'NPWP Card',
      children: detail?.kyc?.npwp_card || 'N/A',
    },
    {
      key: '4',
      label: 'Date of Birth',
      children: detail?.kyc?.date_of_birth || 'N/A',
    },
    {
      key: '5',
      label: 'Occupation',
      children: detail?.kyc?.occupation || 'N/A',
    },
    {
      key: '7',
      label: 'Average Yearly Income',
      children: detail?.kyc?.average_yearly_income || 'N/A',
    },
    {
      key: '8',
      label: 'Source Of Fund',
      children: detail?.kyc?.source_of_fund || 'N/A',
    },
    {
      key: '9',
      label:  'Purpose Of Account Opening',
      children: detail?.kyc?.purpose_of_account_opening || 'N/A',
    },
  ];

  const NameTip = () => (
    <Tooltip title="The name will change to follow the identity name">
      <span
        role="img"
        aria-label="question-circle"
        title=""
        className="anticon anticon-question-circle ant-form-item-tooltip"
      >
        <svg
          viewBox="64 64 896 896"
          focusable="false"
          data-icon="question-circle"
          width="1em"
          height="1em"
          fill="currentColor"
          aria-hidden="true"
        >
          <path d="M512 64C264.6 64 64 264.6 64 512s200.6 448 448 448 448-200.6 448-448S759.4 64 512 64zm0 820c-205.4 0-372-166.6-372-372s166.6-372 372-372 372 166.6 372 372-166.6 372-372 372z"></path>
          <path d="M623.6 316.7C593.6 290.4 554 276 512 276s-81.6 14.5-111.6 40.7C369.2 344 352 380.7 352 420v7.6c0 4.4 3.6 8 8 8h48c4.4 0 8-3.6 8-8V420c0-44.1 43.1-80 96-80s96 35.9 96 80c0 31.1-22 59.6-56.1 72.7-21.2 8.1-39.2 22.3-52.1 40.9-13.1 19-19.9 41.8-19.9 64.9V620c0 4.4 3.6 8 8 8h48c4.4 0 8-3.6 8-8v-22.7a48.3 48.3 0 0130.9-44.8c59-22.7 97.1-74.7 97.1-132.5.1-39.3-17.1-76-48.3-103.3zM472 732a40 40 0 1080 0 40 40 0 10-80 0z"></path>
        </svg>
      </span>
    </Tooltip>
  );

  const items: DescriptionsProps["items"] = [
    {
      key: "0",
      label: "UID",
      children: detail.uid,
    },
    {
      key: "1",
      label: (
        <p>
          Full Name <NameTip />{" "}
        </p>
      ),
      children: detail.full_name,
    },
    {
      key: "2",
      label: "Email",
      children: detail.email,
    },
    {
      key: "3",
      label: "Status",
      children:
          detail.status === 'inactive' ? (
              <Tag color="geekblue">Inactive</Tag>
          ) : detail.status === 'active' ? (
              <Tag color="green">Active</Tag>
          ) : detail.status === 'banned' ? (
              <Tag color="red">Banned</Tag>
          ) : (
              <Tag color="geekblue">Unknown</Tag>
          ),
    },
    {
      key: "5",
      label: "Risk",
      children: detail?.risk ? (
        <Space>
          <AlertTwoTone style={{ fontSize: 16 }} twoToneColor="#eb2f96" />
          {detail.risk_note}
        </Space>
      ) : (
        <Space>
          <NotificationOutlined style={{ fontSize: 16 }} />
          Not Risk
        </Space>
      ),
    },
    {
      key: "6",
      label: "Join At",
      children: FormatDateTime(detail?.created_at),
    },
    {
      key: "7",
      label: "Phone Number",
      children: `62${detail?.phone_number}`,
    },
  ];


  const { token } = theme.useToken();

  return (
    <LayoutDashboard
      title="Member Detail"
      subTitle="Member Detail"
      actions={[]}
      adminLevel={adminLevel}
    >
      <Descriptions items={items} />
      <Divider />
      <If condition={!(detail.kyc === null)}>
        <Then>
          <Row>
            <Col
              span={6}
              style={{
                display: "flex",
                justifyContent: "space-around",
                alignItems: "center",
              }}
            >
              <If condition={loadingPage}>
                <Then>
                  <Skeleton.Image
                    style={{ height: 150, width: 250 }}
                    active={true}
                  />
                </Then>
              </If>
              <If condition={!loadingPage}>
                <Then>
                  <Card
                      cover={<Image
                          width={250}
                          alt="Selfie"
                          src={detail?.kyc?.image_selfie}
                          fallback="data:image/png;base64,"
                      />}
                  >
                    <Meta style={{textAlign: "center"}} title="Selfie" />
                  </Card>
                </Then>
              </If>
            </Col>

            <Col
                span={6}
                style={{
                  display: "flex",
                  justifyContent: "space-around",
                  alignItems: "center",
                }}
            >
              <If condition={loadingPage}>
                <Then>
                  <Skeleton.Image
                      style={{ height: 150, width: 250 }}
                      active={true}
                  />
                </Then>
              </If>
              <If condition={!loadingPage}>
                <Then>
                  <Card
                      cover={<Image
                          width={250}
                          alt="Liveness"
                          src={detail?.kyc?.image_liveness}
                          fallback="data:image/png;base64,"
                      />}
                  >
                    <Meta style={{textAlign: "center"}} title="Liveness" />
                  </Card>
                </Then>
              </If>
            </Col>
            <Col
                span={6}
                style={{
                  display: "flex",
                  justifyContent: "space-around",
                  alignItems: "center",
                }}
            >
              <If condition={loadingPage}>
                <Then>
                  <Skeleton.Image
                      style={{ height: 150, width: 250 }}
                      active={true}
                  />
                </Then>
              </If>
              <If condition={!loadingPage}>
                <Then>
                  <Card
                      cover={<Image
                          width={250}
                          alt="KTP"
                          src={detail?.kyc?.image_ktp}
                          fallback="data:image/png;base64,"
                      />}
                  >
                    <Meta style={{textAlign: "center"}} title="KTP" />
                  </Card>
                </Then>
              </If>
            </Col>
            <Col
              span={6}
              style={{
                display: "flex",
                justifyContent: "space-around",
                alignItems: "center",
              }}
            >
              <If condition={loadingPage}>
                <Then>
                  <Skeleton.Image
                    style={{ height: 150, width: 250 }}
                    active={true}
                  />
                </Then>
              </If>
              <If condition={!loadingPage}>
                <Then>
                  <Card
                      cover={<Image
                          width={250}
                          alt="NPWP"
                          src={detail?.kyc?.image_npwp}
                          fallback="data:image/png;base64,"
                      />}
                  >
                    <Meta style={{textAlign: "center"}} title="NPWP" />
                  </Card>
                </Then>
              </If>
            </Col>
          </Row>
          <Divider />
          <Descriptions  bordered items={details} />
        </Then>
        <Else>
          <Result
            status="404"
            title="KYC Data Not Found"
            subTitle="Sorry, the KYC data you are looking for cannot be found."
          />
        </Else>
      </If>
    </LayoutDashboard>
  );
};

export default DetailPage;
